const bcrypt = require ('bcrypt');
const jwt = require('jsonwebtoken');

const { MongoClient } = require("mongodb");
const client = new MongoClient(process.env.MONGODB_URI, {
    useNewUrlParser: true,
    useUnifiedTopology: true,
});

// Connect to the MongoDB database
(async () => {
    try {
        console.log("Connecting to:", process.env.MONGODB_URI);
        await client.connect();
        console.log("Connected to MongoDB");
    } catch (err) {
        console.error("Error connecting to the database:", err);
    }
})();


// Make sure to close the connection when the app exits
process.on("SIGINT", () => {
    client.close().then(() => {
        console.log("MongoDB connection closed.");
        process.exit(0);
    });
});
exports.login = async (req, res) => {
    try {
        const { email, password } = req.body;
        if (!email || !password) {
            return res.status(400).render("login", {
                message: "Please provide an email and password",
            });
        }

        const usersCollection = client.db("vue-db").collection("users");
        const user = await usersCollection.findOne({ email });

        if (!user || !(await bcrypt.compare(password, user.password))) {
            res.status(401).render("login", {
                message: "Email or password is incorrect",
            });
        } else {
            const id = user._id;
            const token = jwt.sign({ id }, process.env.JWT_SECRET, {
                expiresIn: process.env.JWT_EXPIRES_IN,
            });
            console.log("The token is: " + token);
            const cookieOptions = {
                expires: new Date(Date.now() + process.env.JWT_COOKIES_EXPIRES * 24 * 60 * 60 * 1000),
                httpOnly: true,
            };
            res.cookie("jwt", token, cookieOptions);
            res.status(200).redirect("/");
        }
    } catch (error) {
        console.log(error);
    }
};
exports.register = async (req, res) => {
    console.log(req.body);

    const { name, email, password, passwordConfirm } = req.body;

    const usersCollection = client.db("vue-db").collection("users");

    try {
        const existingUser = await usersCollection.findOne({ email });
        if (existingUser) {
            return res.render("register", {
                message: "That email is already in use.",
            });
        } else if (password !== passwordConfirm) {
            return res.render("register", {
                message: "Passwords do not match",
            });
        }

        const hashedPassword = await bcrypt.hash(password, 8);
        console.log(hashedPassword);

        const newUser = {
            name: name,
            email: email,
            password: hashedPassword,
        };

        await usersCollection.insertOne(newUser);

        return res.render("register", {
            message: "User registered",
        });
    } catch (error) {
        console.log(error);
        res.status(500).send("Server Error");
    }
};

exports.logout = async (req, res) => {
        res.clearCookie("jwt"); // Clear the JWT cookie from the user's browser
        res.status(200).redirect("/login"); // Redirect the user to the login page after logout
};
